import {DATOS_API, OBTENER_NOMBRE} from "../type";


export default (state, action) => {
    switch (action.type) {
        case OBTENER_NOMBRE:
            return{
                ...state,
                nombre : action.payload.nombrep
            }
        case DATOS_API:
            return{
                ...state,
                resultado: action.payload.data
            }
        default:
            return state;
    }
}
