import React, { useReducer, useEffect } from 'react';
import pokemonContext from "./pokemonContext";
import pokemonReducer from "./pokemonReducer";
import axios from "axios";
import {OBTENER_NOMBRE} from "../type";

const PokemonState = (props) => {

    const initialstate = {
        nombre: null,
        resultado: null
    }

    const [state, dispatch] = useReducer(pokemonReducer, initialstate);

    const obtenerNombre = async nombre => {
        dispatch({
            type: OBTENER_NOMBRE,
            payload: nombre
        });
    }

    useEffect(() => {
        const llamandoAPI = async () => {
            const url = `https://pokeapi.co/api/v2/pokemon/${state.nombre}`;
    
            const datoobtenido = await axios.get(url);
            console.log(datoobtenido);
        }
    }, [state.nombre]);

    return ( 
        <pokemonContext.Provider
            value={{
                obtenerNombre
            }}
        >
            {props.children}
        </pokemonContext.Provider>
     );
}
 
export default PokemonState;
