import React, { useContext, useState } from 'react';
import Listadopokemon from "./Listadopokemon";
import pokemonContext from "../../context/pokemonContext";

const Form = () => {

    const PokemonContext = useContext(pokemonContext);
    const {obtenerNombre} = PokemonContext;

    //Crewando state para guatrdar el nombre del pokemon
    const [pokemon, guardarpokemon] = useState({
        nombreP : ""
    });
    const {nombreP} = pokemon;

    //Funcion text box guardar nombre 
    const oncambios = (e) => {
        guardarpokemon({
            ...pokemon,
            [e.target.name] : e.target.value
        });
    }

    //funcion boton
    const onConsultar  = () =>{
        alert("hola");
    }

    return ( 
        <form>
            <div>
                <label>Ingresa el pokemon a buscar</label>
                <input
                    type="text"
                    nombre = "nombreP"
                    value = {nombreP}
                    placeholder="Ingresa el nombre de un pokemon"
                    onChange={onConsultar}
                />
                <button
                    onClick={onConsultar}
                >Consultar</button>
            </div>
            
            <Listadopokemon/>
        </form>
    );
}
 
export default Form;
